IF (select Count(*) from sys.symmetric_keys where name like '%DatabaseMasterKey%') = 0

BEGIN

CREATE master key;

IF NOT EXISTS ( SELECT  * FROM sys.schemas WHERE name = N'BritishColumbia' )

    EXEC('CREATE SCHEMA [BritishColumbia] AUTHORIZATION [dbo]');
select Count(*) from sys.symmetric_keys;

END

ELSE 

BEGIN
IF NOT EXISTS ( SELECT  * FROM sys.schemas WHERE name = N'BritishColumbia' )

    EXEC('CREATE SCHEMA [BritishColumbia] AUTHORIZATION [dbo]');

select Count(*) from sys.symmetric_keys;

END

